import React, { useState } from "react";
import { Button, Card, CardContent, createTheme, CssBaseline,Fab, FormControl, InputLabel, Input, ThemeProvider } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import "./styles.css"; // Import the CSS file

const darkTheme = createTheme({
  palette: {
    mode: "dark",
  },
});

const App = () => {
  const [showForm, setShowForm] = useState(false);
  const [streamData, setStreamData] = useState({
    streamName: "",
    streamUrl: "",
    streamType: "",
  });
  const [successMessage, setSuccessMessage] = useState("");

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Add logic to save the data and show a success message here
    // For this example, we'll just set a success message.
    setSuccessMessage("Stream data saved successfully!");
  };

  return (
    <div>
      <CssBaseline />
      <div className="container">

        {/* Add a Fab (Floating Action Button) */}
        <Fab
          color="color"
          onClick={() => setShowForm(true)}
        >
          <AddIcon />
        </Fab>

      </div>

      {showForm && (
        <Card className="card">
          <CardContent>
            <form onSubmit={handleFormSubmit}>
              <FormControl fullWidth>
                <InputLabel>Stream Name</InputLabel>
                <Input
                  required
                  value={streamData.streamName}
                  onChange={(e) =>
                    setStreamData({ ...streamData, streamName: e.target.value })
                  }
                />
              </FormControl>
              <br />
              <br/>

              <FormControl fullWidth className="form-control">

                <InputLabel>Stream URL</InputLabel>
                <Input
                  required
                  value={streamData.streamUrl}
                  onChange={(e) =>
                    setStreamData({ ...streamData, streamUrl: e.target.value })
                  }
                />
              </FormControl>
              <br />
              <br/>

              <FormControl fullWidth className="form-control">

                <InputLabel>Stream Type</InputLabel>

                <Input
                  required
                  value={streamData.streamType}
                  onChange={(e) =>
                    setStreamData({ ...streamData, streamType: e.target.value })
                  }
                />
              </FormControl>
              <br/>
              <br />


                <Button
                  variant="contained"
                  color="primary"
                  type="submit"
                  className="button"
                >
                  Save Stream
                </Button>
                <span> </span>

                <Button
                  variant="contained"
                  color="primary"
                  type="submit"
                  className="button"
                >
                  Cancel Stream
                </Button>

            </form>
          </CardContent>
        </Card>
      )}

      {successMessage && (
        <Card className="card">
          <CardContent>
            {successMessage}
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default App;
