import React, { useState, useEffect } from 'react';
import {
  Avatar,
  Drawer,
  AppBar,
  List,
  CssBaseline,
  Divider,
  IconButton,
  Typography,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  ThemeProvider,
} from '@mui/material';
import {
  ChevronLeft,
  ChevronRight,
  MoveToInbox,
  Mail,
  Person,
} from '@mui/icons-material';
import axios from 'axios';
import logo from "./assets/secura-f.png"
import { createTheme } from '@mui/material/styles';

const drawerWidth = 240;

const darkTheme = createTheme({
  palette: {
    mode: 'dark', 
  },
});

const DrawerHeader = ({ theme, handleDrawerClose }) => (
  <div
    style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'flex-end',
      padding: theme.spacing(5, 10),
      ...theme.mixins.toolbar,
    }}
  ></div>
);

const UserIcon = ({ userData, toggleDetails }) => (
  <div
    style={{
      display: 'flex',
      alignItems: 'center',
      marginLeft: 'auto',
      marginRight: '16px',
    }}
  >
    <Avatar>{userData.name ? userData.name[0] : 'U'}</Avatar>
    <IconButton onClick={toggleDetails} sx={{ width: '15px', height: '15px' }}>
      {userData.showDetails ? <Person /> : <Person />}
    </IconButton>
    {userData.showDetails && (
      <div style={{ marginLeft: '10px' }}>
        <p>
          Name: {userData.name}
          <br />
          Email: {userData.email}
        </p>
      </div>
    )}
  </div>
);

const MiniDrawer = () => {
  const [open, setOpen] = useState(false);
  const [userData, setUserData] = useState({ name: '', email: '', showDetails: false });

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const toggleDetails = () => {
    setUserData((prevUserData) => ({
      ...prevUserData,
      showDetails: !prevUserData.showDetails,
    }));
  };

  useEffect(() => {
    axios
      .get('https://jsonplaceholder.typicode.com/users/1')
      .then((response) => {
        setUserData({ ...response.data, showDetails: false });
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  return (
    <ThemeProvider theme={darkTheme}>
      <div style={{ display: 'flex' }}>
        <CssBaseline />
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={open ? handleDrawerClose : handleDrawerOpen}
        >
          {open ? <ChevronLeft /> : <ChevronRight />}
        </IconButton>
        <Drawer
          open={open}
          sx={{
            width: drawerWidth,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: drawerWidth,
              boxSizing: 'border-box',
              backgroundColor: darkTheme.palette.background.default, // Set the background color
            },
          }}
        >
          <DrawerHeader theme={darkTheme} handleDrawerClose={handleDrawerClose} />
          <Divider />
          <List>
            {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, index) => (
              <ListItem key={text} disablePadding sx={{ display: 'block' }}>
                <ListItemButton
                  sx={{
                    minHeight: 48,
                    justifyContent: open ? 'initial' : 'center',
                    px: 2.5,
                  }}
                >
                  <ListItemIcon
                    sx={{
                      minWidth: 0,
                      mr: open ? 3 : 'auto',
                      justifyContent: 'center',
                    }}
                  >
                    {index % 2 === 0 ? <MoveToInbox /> : <Mail />}
                  </ListItemIcon>
                  {open ? <ListItemText primary={text} /> : null}
                </ListItemButton>
              </ListItem>
            ))}
          </List>
          <Divider />
          <List>
            {['All mail', 'Trash', 'Spam'].map((text, index) => (
              <ListItem key={text} disablePadding sx={{ display: 'block' }}>
                <ListItemButton
                  sx={{
                    minHeight: 48,
                    justifyContent: open ? 'initial' : 'center',
                    px: 2.5,
                  }}
                >
                  <ListItemIcon
                    sx={{
                      minWidth: 0,
                      mr: open ? 3 : 'auto',
                      justifyContent: 'center',
                    }}
                  >
                    {index % 2 === 0 ? <MoveToInbox /> : <Mail />}
                  </ListItemIcon>
                  {open ? <ListItemText primary={text} /> : null}
                </ListItemButton>
              </ListItem>
            ))}
          </List>
        </Drawer>
        <div>
          <AppBar
            position="fixed"
            open={open}
            sx={{
              zIndex: darkTheme.zIndex.drawer + 1,
              display: 'flex',
              justifyContent: 'space-between',
              transition: darkTheme.transitions.create(['width', 'margin'], {
                easing: darkTheme.transitions.easing.sharp,
                duration: darkTheme.transitions.duration.leavingScreen,
              }),
            }}
          >
            <div style={{ display: 'flex' }}>
              <img
                src={logo}
                alt="logo"
                style={{
                  width: '130px',
                  height: '30px',
                  cursor: 'pointer',
                }}
                onClick={open ? handleDrawerClose : handleDrawerOpen}
              />
              <UserIcon userData={userData} toggleDetails={toggleDetails} />
            </div>
          </AppBar>
        </div>
      </div>
    </ThemeProvider>
  );
};

export default MiniDrawer;
