import React, { useState, useEffect } from 'react';
import { Box, CssBaseline, Avatar, Typography, List, ListItem, ListItemIcon, ListItemText, Divider,  } from '@mui/material';
import {  LiveTv, ViewList, Web, MoreVert, Settings, ExitToApp } from '@mui/icons-material';

function App() {
  // Simulated user data (replace with actual API fetch)
  const [userData, setUserData] = useState({ name: 'John Doe', avatar: 'https://example.com/avatar.png' });

  useEffect(() => {
    // Simulated API call
    // Replace this with your actual API call to fetch user data
    setTimeout(() => {
      setUserData({ name: 'John Doe', avatar: 'https://example.com/avatar.png' });
    }, 2000);
  }, []);

  return (
    <div style={{ display: 'flex' }}>
      <CssBaseline />
      {/* Sidebar 1 (Logo) */}
      <div style={{ width: 100, backgroundColor: '#151515' }}>
        {/* Logo goes here */}
      </div>
      {/* Sidebar 2 (Menu) */}
      <div style={{ width: 218, backgroundColor: '#0D0D0F', color: 'white' }}>
        {/* User Info */}
        <Box p={2} display="flex" alignItems="center">
          <Avatar src={userData.avatar} alt={userData.name} />
          <Box ml={2}>
            <Typography variant="subtitle1">{userData.name}</Typography>
          </Box>
        </Box>
        {/* Navigation Menu */}
        <List>
          <ListItem button>
            <ListItemIcon><LiveTv /></ListItemIcon>
            <ListItemText primary="Live" />
          </ListItem>
          <ListItem button>
            <ListItemIcon><ViewList /></ListItemIcon>
            <ListItemText primary="Sequence" />
          </ListItem>
          <ListItem button>
            <ListItemIcon><Web /></ListItemIcon>
            <ListItemText primary="Layout" />
          </ListItem>
          <ListItem button>
            <ListItemIcon><MoreVert /></ListItemIcon>
            <ListItemText primary="More" />
          </ListItem>
        </List>
        {/* Settings and Logout */}
        <Divider />
        <List>
          <ListItem button>
            <ListItemIcon><Settings /></ListItemIcon>
            <ListItemText primary="Settings" />
          </ListItem>
          <ListItem button>
            <ListItemIcon><ExitToApp /></ListItemIcon>
            <ListItemText primary="Logout" />
          </ListItem>
        </List>
      </div>
    </div>
  );
}

export default App;
