import React, { useState } from 'react';
import VideoJS from './Components/videojs';
import videoSources from '../../Data/source.json';
import { Container, Grid, IconButton, Card, CardContent, Typography } from '@mui/material';
import WindowSharpIcon from '@mui/icons-material/WindowSharp';
import ViewModuleSharpIcon from '@mui/icons-material/ViewModuleSharp';
import ViewAgendaSharpIcon from '@mui/icons-material/ViewAgendaSharp';

const Video = () => {
  const [numCols, setNumCols] = useState(3);

  const handleNumColsChange = (cols) => {
    setNumCols(cols);
  };

  const players = {};

  const handlePlayerReady = (player, source) => {
    players[source.label] = player;
    player.on('waiting', () => {
      console.log('Player is waiting for ' + source.label);
    });

    player.on('dispose', () => {
      console.log('Player will dispose for ' + source.label);
    });
  };

  return (
    <Container>
      <div className='body'>
        <div className="flex-container">
          {[1, 2, 3].map((cols) => (
            <IconButton
              key={cols}
              onClick={() => handleNumColsChange(cols)}
              className={cols === numCols ? 'icon-button-active' : 'icon-button'}
            >
              {cols === 1 ? <ViewAgendaSharpIcon /> : cols === 2 ? <WindowSharpIcon /> : <ViewModuleSharpIcon />}
            </IconButton>
          ))}
        </div>
      </div>
      <div>
        <Grid container spacing={2}>
          {videoSources.map((source, index) => (
            <Grid item xs={12 / Math.min(numCols, videoSources.length)} key={index}>
              <Card className="custom-card" style={{ backgroundColor: '#111115', border: 'none', borderRadius: '0', color: 'white' }}>
                <div className="video-container">
                  <VideoJS
                    options={{
                      autoplay: true,
                      controls: true,
                      responsive: true,
                      fluid: true,
                      sources: [
                        {
                          src: source.src,
                          type: 'application/x-mpegURL',
                        },
                      ],
                    }}
                    onReady={(player) => handlePlayerReady(player, source)}
                  />
                </div>
                <CardContent>
                  <Typography>Camera 1</Typography>
                  {/* Add your content here */}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </div>
    </Container>
  );
};

export default Video;
