import React, { useState, useEffect } from 'react';
import {
  Avatar,
  Drawer,
  AppBar,
  List,
  CssBaseline,
  IconButton,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  ThemeProvider,
} from '@mui/material';
import { ChevronLeft, ChevronRight, MoveToInbox, Mail } from '@mui/icons-material';
import ArrowDropDownCircleOutlinedIcon from '@mui/icons-material/ArrowDropDownCircleOutlined';
import axios from 'axios';
import { createTheme } from '@mui/material/styles';
import { Link } from 'react-router-dom';
import './App.css';

const drawerWidth = 240;

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
  },
});

const DrawerHeader = ({ theme, handleDrawerClose }) => (
  <div
    style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'flex-end',
      padding: theme.spacing(0, 1), // Adjust the padding
      ...theme.mixins.toolbar,
    }}
  >
    <IconButton onClick={handleDrawerClose}>
      {theme.direction === 'rtl' ? <ChevronRight /> : <ChevronLeft />}
    </IconButton>
  </div>
);

const UserIcon = ({ userData, toggleDetails }) => (
  <div
    style={{
      display: 'flex',
      alignItems: 'center',
    }}
  >
    <div style={{ marginRight: '10px' }}>
      {userData.name}
    </div>
    <ArrowDropDownCircleOutlinedIcon sx={{ cursor: 'pointer', marginRight: '30px' }} />
    <Avatar sx={{ cursor: 'pointer' }}>{userData.name ? userData.name[0] : 'U'}</Avatar>
  </div>
);

const MiniDrawer = () => {
  const [open, setOpen] = useState(false);
  const [userData, setUserData] = useState({ name: '', email: '', showDetails: false });

  const handleDrawerOpen = () => setOpen(true);
  const handleDrawerClose = () => setOpen(false);

  const toggleDetails = () => {
    setUserData((prevUserData) => ({
      ...prevUserData,
      showDetails: !prevUserData.showDetails,
    }));
  };

  useEffect(() => {
    axios
      .get('https://jsonplaceholder.typicode.com/users/1')
      .then((response) => {
        setUserData({ ...response.data, showDetails: false });
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  return (
    <ThemeProvider theme={darkTheme}>
      <div style={{ display: 'flex' }}>
        <CssBaseline />
        <AppBar
          position="fixed"
          open={open}
          sx={{
            width: `calc(100% - ${drawerWidth}px)`,
            marginLeft: open ? `${drawerWidth}px` : '0', // Adjust the layout when the drawer is open
            zIndex: (theme) => theme.zIndex.drawer + 1,
            transition: darkTheme.transitions.create(['width', 'margin'], {
              easing: darkTheme.transitions.easing.sharp,
              duration: darkTheme.transitions.duration.leavingScreen,
            }),
          }}
        >
          <div style={{ display: 'flex', padding: '20px' }}>
            <UserIcon userData={userData} toggleDetails={toggleDetails} />
          </div>
        </AppBar>
        <Drawer
          open={open}
          sx={{
            width: drawerWidth,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: drawerWidth,
              backgroundColor: darkTheme.palette.background.default,
            },
          }}
        >
          <DrawerHeader theme={darkTheme} handleDrawerClose={handleDrawerClose} />
          <List>
            {[
              { text: 'Home', route: '/' },
              { text: 'Starred', route: '' },
              { text: 'Camera List', route: '' },
              { text: 'Layout', route: '/layout' },
            ].map((item, index) => (
              <ListItem key={item.text} disablePadding sx={{ display: 'block' }}>
                <Link to={item.route} sx={{ textDecoration: 'none' }}>
                  <ListItemButton
                    sx={{
                      minHeight: 48,
                      justifyContent: 'center',
                      px: 2.5,
                    }}
                  >
                    <ListItemIcon
                      sx={{
                        minWidth: 0,
                        mr: open ? 3 : 'auto',
                        justifyContent: 'center',
                      }}
                    >
                      {index % 2 === 0 ? <MoveToInbox /> : <Mail />}
                    </ListItemIcon>
                    {open && (
                      <ListItemText primary={item.text} className="no-underline-color" />
                    )}
                  </ListItemButton>
                </Link>
              </ListItem>
            ))}
          </List>
        </Drawer>
      </div>
    </ThemeProvider>
  );
};

export default MiniDrawer;
