import React from 'react';
import VideoJS from './videojs'; // Import your VideoJS component
import videoSources from './source'; // Import the JSON file

const Video = () => {
  const players = {}; // To store references to multiple players

  const handlePlayerReady = (player, source) => {
    players[source.label] = player;
    player.on('waiting', () => {
      console.log('Player is waiting for ' + source.label);
    });

    player.on('dispose', () => {
      console.log('Player will dispose for ' + source.label);
    });
  };

  return (
    <div>
      {videoSources.map((source, index) => (
        <div key={index} style={{ width: "30vw", height: "20vh", margin: '10px' }}>
          <h2>{source.label}</h2>
          <VideoJS
            options={{
              autoplay: true,
              muted: true,
              controls: true,
              responsive: true,
              fluid: true,
              sources: [
                {
                  src: source.src,
                  type: 'application/x-mpegURL',
                },
              ],
            }}
            onReady={(player) => handlePlayerReady(player, source)}
          />
        </div>
      ))}
    </div>
  );
};

export default Video;
