import React, { useState } from "react";
import { Button, Card, CardContent,Fab, FormControl, InputLabel, Input} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import "./styles.css"; // Import the CSS file

const App = () => {
  const [showForm, setShowForm] = useState(false);
  const [streamData, setStreamData] = useState({
    streamName: "",
    streamUrl: "",
    streamType: "",
  });
  const [successMessage, setSuccessMessage] = useState("");

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Add logic to save the data and show a success message here
    // For this example, we'll just set a success message.
    setSuccessMessage("Stream data saved successfully!");
  };

  return (
    <div>
      <div className="container">
        <Fab
          style={{
            position: 'fixed',
            bottom: '30px',
            right: '30px',
          }}
          onClick={() => setShowForm(true)}
        >
          <AddIcon />
        </Fab>
      </div>

      {showForm && (
        <Card className="card" sx={{backgroundColor:'black'}}>
          <CardContent>
            <form onSubmit={handleFormSubmit}>
              <FormControl fullWidth>
                <InputLabel sx={{ color: 'white' }}>Stream Name</InputLabel>
                <Input
                  required
                  value={streamData.streamName}
                  onChange={(e) =>
                    setStreamData({ ...streamData, streamName: e.target.value })
                  }
                  sx={{ color: 'white' }} // Set input text color
                />
              </FormControl>
              <br />
              <br/>

              <FormControl fullWidth className="form-control" >
                <InputLabel sx={{ color: 'white' }}>Stream URL</InputLabel>
                <Input
                  required
                  value={streamData.streamUrl}
                  onChange={(e) =>
                    setStreamData({ ...streamData, streamUrl: e.target.value })
                  }
                  sx={{ color: 'white' }} // Set input text color
                />
              </FormControl>
              <br />
              <br/>

              <FormControl fullWidth className="form-control">
                <InputLabel sx={{ color: 'white' }}>Stream Type</InputLabel>
                <Input
                  required
                  value={streamData.streamType}
                  onChange={(e) =>
                    setStreamData({ ...streamData, streamType: e.target.value })
                  }
                  sx={{ backgroundcolor: 'white' }} // Set input text color
                />
              </FormControl>
              <br/>
              <br />

                <Button
                  variant="contained"
                  color="primary"
                  type="submit"
                  className="button"
                >
                  Save Stream
                </Button>
                <span> </span>

                <Button
                  variant="contained"
                  color="primary"
                  type="submit"
                  className="button"
                >
                  Cancel Stream
                </Button>

            </form>
          </CardContent>
        </Card>
      )}

      {successMessage && (
        <Card className="card">
          <CardContent>
            {successMessage}
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default App;
