import React, { useState, useRef, useEffect } from 'react';
import videojs from 'video.js';
import 'video.js/dist/video-js.css';
import { Container, Grid, IconButton, Card, CardContent, Typography } from '@mui/material';
import WindowSharpIcon from '@mui/icons-material/WindowSharp';
import ViewModuleSharpIcon from '@mui/icons-material/ViewModuleSharp';
import ViewAgendaSharpIcon from '@mui/icons-material/ViewAgendaSharp';

import videoSources from '../../Data/source.json'; // Adjust the path as needed

const LiveComponent = () => {
  const [numCols, setNumCols] = useState(3);
  const players = {};

  const handleNumColsChange = (cols) => {
    setNumCols(cols);
  };

  const handlePlayerReady = (player, source) => {
    players[source.label] = player;
    player.on('waiting', () => {
      console.log('Player is waiting for ' + source.label);
    });

    player.on('dispose', () => {
      console.log('Player will dispose for ' + source.label);
    });
  };

  const createVideoPlayer = (options, source) => {
    const videoRef = useRef(null);
    const playerRef = useRef(null);

    useEffect(() => {
      if (!playerRef.current) {
        const videoElement = document.createElement('video');
        videoElement.classList.add('video-js', 'vjs-big-play-centered');
        videoRef.current.appendChild(videoElement);

        const player = playerRef.current = videojs(videoElement, options, () => {
          videojs.log('player is ready');
          handlePlayerReady(player, source);
        });
      } else {
        const player = playerRef.current;
        player.autoplay(options.autoplay);
        player.src(options.sources);
      }

      return () => {
        const player = playerRef.current;
        if (player && !player.isDisposed()) {
          player.dispose();
          playerRef.current = null;
        }
      };
    }, [options, videoRef, source]);

    return (
      <div data-vjs-player>
        <div ref={videoRef} />
      </div>
    );
  };

  return (
    <Container>
      <Grid container>
        {videoSources.map((source, index) => (
          <Grid item xs={numCols} key={index}>
            <Card>
              <CardContent>
                <Typography variant="h6">{source.label}</Typography>
                {createVideoPlayer(
                  {
                    autoplay: true,
                    muted: true,
                    controls: true,
                    responsive: true,
                    fluid: true,
                    sources: [
                      {
                        src: source.src,
                        type: 'application/x-mpegURL',
                      },
                    ],
                  },
                  source
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default LiveComponent;
