import React, { useState, useRef, useEffect } from 'react';
import videojs from 'video.js';
import 'video.js/dist/video-js.css';
import { Container, Grid, IconButton, Card, CardContent, Typography } from '@mui/material';
import WindowSharpIcon from '@mui/icons-material/WindowSharp';
import ViewModuleSharpIcon from '@mui/icons-material/ViewModuleSharp';
import ViewAgendaSharpIcon from '@mui/icons-material/ViewAgendaSharp';
import videoSources from './source'; // Import the JSON file
import './styles.css';

const VideoPlayer = () => {
  const [numCols, setNumCols] = useState(3);
  const videoRefs = useRef({});
  const players = useRef({});

  const handleNumColsChange = (cols) => {
    setNumCols(cols);
  };

  const handlePlayerReady = (player, source) => {
    players.current[source.label] = player;
    player.on('waiting', () => {
      console.log('Player is waiting for ' + source.label);
    });

    player.on('dispose', () => {
      console.log('Player will dispose for ' + source.label);
    });
  };

  useEffect(() => {
    videoSources.forEach((source) => {
      const options = {
        autoplay: true,
        controls: true,
        responsive: true,
        fluid: true,
        sources: [
          {
            src: source.src,
            type: 'application/x-mpegURL',
          },
        ],
      };

      if (!videoRefs.current[source.label]) {
        videoRefs.current[source.label] = videojs(document.createElement("video"), options, () => {
          handlePlayerReady(videoRefs.current[source.label], source);
        });
      } else {
        const player = videoRefs.current[source.label];
        player.autoplay(true);
        player.src(options.sources);
      }
    });

    return () => {
      Object.keys(videoRefs.current).forEach((label) => {
        const player = videoRefs.current[label];
        if (player && !player.isDisposed()) {
          player.dispose();
          delete videoRefs.current[label];
        }
      });
    };
  }, []);

  return (
    <Container>
      <div className='body'>
        <div className="flex-container">
          {[1, 2, 3].map((cols) => (
            <IconButton
              key={cols}
              onClick={() => handleNumColsChange(cols)}
              className={cols === numCols ? 'icon-button-active' : 'icon-button'}
            >
              {cols === 1 ? <ViewAgendaSharpIcon /> : cols === 2 ? <WindowSharpIcon /> : <ViewModuleSharpIcon />}
            </IconButton>
          ))}
        </div>
      </div>
      <div>
        <Grid container spacing={2}>
          {videoSources.map((source, index) => (
            <Grid item xs={12 / Math.min(numCols, videoSources.length)} key={index}>
              <Card className="custom-card" style={{ backgroundColor: '#111115', border: 'none', borderRadius: '0', color: 'white' }}>
                <div className="video-container">
                  <div data-vjs-player>
                    <div ref={(el) => (videoRefs.current[source.label] = el)} />
                  </div>
                </div>
                <CardContent>
                  <Typography>{source.label}</Typography>
                  {/* Add your content here */}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </div>
    </Container>
  );
};

export default VideoPlayer;
