import React, { useState } from 'react';
import Toolbar from '@mui/material/Toolbar';
import InputBase from '@mui/material/InputBase';
import Hidden from '@mui/material/Hidden';
import customSearchIcon from './assets/Search.png';
import customNotificationsIcon from './assets/Group.png';
import customNotificationsIconWithMessage from './assets/Message.png';

const SearchBox = () => {
  const [hasNewMessage, setHasNewMessage] = useState(false);
  const [notificationIcon, setNotificationIcon] = useState(customNotificationsIcon);
  const [searchVisible, setSearchVisible] = useState(false);
  const [showNotificationBox, setShowNotificationBox] = useState(false);

  const toggleSearch = () => {
    setSearchVisible(!searchVisible);
  };

  const toggleNotificationBox = () => {
    setShowNotificationBox(!showNotificationBox);
  };

  const toggleNotificationIconClick = () => {
    if (!showNotificationBox) {
      setNotificationIcon(customNotificationsIconWithMessage);
      setShowNotificationBox(true);
    } else {
      setNotificationIcon(customNotificationsIcon);
      setShowNotificationBox(false);
    }
  };

  return (
    <div position="static" style={{ backgroundColor: 'rgb(17, 17, 21)' }}>
      <Toolbar>
        <div style={{ display: 'flex', alignItems: 'center', width: '100%',float:"right"}}>
          <Hidden>
            <div style={{ flex: 1 }}>
              {searchVisible ? (
                <InputBase
                  placeholder="
                  Search..."
                  inputProps={{ 'aria-label': 'search' }}
                  fullWidth
                  sx={{
                    backgroundColor: 'white',
                    color: 'black',
                    borderRadius: '25px',
                    height: '40px',
                    fontSize: '16px',
                  }}
                />
              ) : null}
            </div>
          </Hidden>
          <Hidden>
            {/* Mobile View (Hidden on screens larger than or equal to 'md') */}
            <div style={{ margin: '0 12px' }}>
              <img
                src={customSearchIcon}
                alt="Search"
                onClick={toggleSearch}
                style={{
                  cursor: 'pointer',
                  width: '30px',
                  height: '30px',
                }}
              />
            </div>
          </Hidden>
          <div style={{ margin: '0 7px', position: 'relative' }}>
            <img
              src={notificationIcon}
              alt="Notifications"
              onClick={toggleNotificationIconClick}
              style={{
                cursor: 'pointer',
                width: '30px',
                height: '30px',
              }}
            />
            {showNotificationBox && (
              <div style={{
                position: 'absolute',
                top: '30px',
                right: '-10px',
                backgroundColor: 'red',
                color: 'white',
                borderRadius: '50%',
                width: '20px',
                height: '20px',
                textAlign: 'center',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
              }}>
              </div>
            )}
            {showNotificationBox && (
              <div style={{
                position: 'absolute',
                top: '30px',
                right: '-10px',
                backgroundColor: 'white',
                color: 'black',
                borderRadius: '5px',
                width: '300px',
                height: '500px',
                onClick:{toggleNotificationIconClick}
              }}>
                empty...!
              </div>
            )}
          </div>
        </div>
      </Toolbar>
      </div>

  );
};

export default SearchBox;
