import React, { useEffect, useRef } from 'react';
import JSMpeg from '@cycjimmy/jsmpeg-player';
import videoUrls from '../../Data/source.json';

const VideoPlayer = () => {
    const wrapper = useRef(null);
    const [streamUrl, setStreamUrl] = useState(null);

    useEffect(() => {
        // Assuming your API endpoint is 'https://example.com/api/stream'
        fetch('https://192.168.1.52:3000/available-ports')
            .then(response => response.json())
            .then(data => {
                setStreamUrl(data.url);
            })
            .catch(error => {
                console.error('Error fetching stream URL:', error);
            });
    }, []);

    useEffect(() => {
        if (streamUrl) {
            const player = new JSMpeg.Player(streamUrl, {
                canvas: wrapper.current,
                autoplay: true,
                videoBufferSize: 512 * 1024,
                audioBufferSize: 128 * 1024,
                controls: true,
            });
        }
    }, [streamUrl]);


    return (
        <div style={{ position: 'relative', paddingBottom: '56.25%', height: '0' }}>
            <canvas ref={wrapper} style={{ position: 'absolute', width: '100%', height: '100%', top: '0', left: '0' }} />
        </div>
    );
};

export default VideoPlayer;
