import React, { useState, useEffect } from 'react';
import {
  Avatar,
  Drawer,
  AppBar,
  List,
  CssBaseline,
  IconButton,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  ThemeProvider,
} from '@mui/material';
import {
  ChevronLeft,
  ChevronRight,
  MoveToInbox,
  Mail,
} from '@mui/icons-material';
import axios from 'axios';
import logo from "./assets/secura-f.png";
import { createTheme } from '@mui/material/styles';
import { Link } from 'react-router-dom';
import './App.css';

const drawerWidth = 40;



const darkTheme = createTheme({
  palette: {
    mode: 'dark',
  },
});

const DrawerHeader = ({ theme}) => (
  <div
    style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'flex-end',
      padding: theme.spacing(5, 4),
      ...theme.mixins.toolbar,
    }}
  ></div>
);



const MiniDrawer = () => {
  const [open, setOpen] = useState(false);
  const [userData, setUserData] = useState({ name: '', email: '', showDetails: false });
  const [isHovered, setIsHovered] = useState(false);
  const handleDrawerOpen = () => setOpen(false);
  const handleDrawerClose = () => setOpen(true);

  const toggleDetails = () => {
    setUserData((prevUserData) => ({
      ...prevUserData,
      showDetails: !prevUserData.showDetails,
    }));
  };
  const UserIcon = ({ userData, toggleDetails}) => (
    <div
      style={{
        display: 'flex',
        alignItems: 'center',
        marginLeft: 'auto',
        marginRight: '16px',
      }}
    >
    <Avatar
      onClick={toggleDetails}
      sx={{ cursor: 'pointer' }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {userData.name ? userData.name[0] : 'U'}
    </Avatar>
    {isHovered && userData.name && (
        <div className="user-name-tooltip" style={{ marginLeft: '10px' }}>{userData.name}</div>
      )}

    </div>
  );

  useEffect(() => {
    axios
      .get('https://jsonplaceholder.typicode.com/users/1')
      .then((response) => {
        setUserData({ ...response.data, showDetails: false });
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  return (
    <ThemeProvider theme={darkTheme}>
      <div style={{ display: 'flex' }}>
        <CssBaseline />
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={open ? handleDrawerClose : handleDrawerOpen}
        >
          {open ? <ChevronLeft /> : <ChevronRight />}
        </IconButton>
        <Drawer
          open={open}
          sx={{
            width: drawerWidth,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: drawerWidth,
              boxSizing: 'border-box',
              backgroundColor: darkTheme.palette.background.default,
            },
          }}
        >
          <DrawerHeader theme={darkTheme} handleDrawerClose={handleDrawerClose} />
          <List>
            {[
              { text: 'Home', route: '/' },
              { text: 'Starred', route: '' },
              { text: 'Camera List', route: '' },
              { text: 'Layout', route: '/layout' },
            ].map((item, index) => (
              <ListItem key={item.text} disablePadding sx={{ display: 'block' }}>
                <Link to={item.route} sx={{ textDecoration: 'none' }}>
                  <ListItemButton
                    sx={{
                      minHeight: 48,
                      justifyContent: 'center',
                      px: 2.5,
                    }}
                  >
                    <ListItemIcon
                      sx={{
                        minWidth: 0,
                        mr: open ? 3 : 'auto', // Show margin when the drawer is open
                        justifyContent: 'center',
                      }}
                    >
                      {index % 2 === 0 ? <MoveToInbox /> : <Mail />}
                    </ListItemIcon>
                    {open && (
                      <ListItemText primary={item.text} className="no-underline-color" />
                    )}
                  </ListItemButton>
                </Link>
              </ListItem>
            ))}
          </List>
        </Drawer>
        <div>
          <AppBar
            position="fixed"
            open={open}
            sx={{
              zIndex: darkTheme.zIndex.drawer + 1,
              display: 'flex',
              justifyContent: 'space-between',
              transition: darkTheme.transitions.create(['width', 'margin'], {
                easing: darkTheme.transitions.easing.sharp,
                duration: darkTheme.transitions.duration.leavingScreen,
              }),
            }}
          >
            <div style={{ display: 'flex', padding: '20px' }}>
              <img
                src={logo}
                alt="logo"
                style={{
                  width: '130px',
                  height: '30px',
                  cursor: 'pointer',
                }}
                onClick={open ? handleDrawerClose : handleDrawerOpen}
              />
              <UserIcon userData={userData} toggleDetails={toggleDetails} />
            </div>
          </AppBar>
        </div>
      </div>
    </ThemeProvider>
  );
};

export default MiniDrawer;
