import React from 'react';
import { Container, Paper, Box, Typography, AppBar, Toolbar, IconButton, Avatar, Menu, MenuItem, Button } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme();

const MainPage = () => {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  return (
    <ThemeProvider theme={theme}>
      <div style={{ backgroundColor: '#111115' }}>
        <Container maxWidth="lg">
          <Box display="flex">
            {/* Sidebar 1: Display Logo */}
            <Paper elevation={3} style={{ width: 200, padding: 20 }}>
              <Typography variant="h6">Logo</Typography>
            </Paper>

            {/* Sidebar 2: Menu and User Info */}
            <Paper elevation={3} style={{ marginLeft: 20, padding: 20, flex: 1 }}>
              {/* Menu Bar */}
              <AppBar position="static" color="transparent">
                <Toolbar>
                  <IconButton
                    color="inherit"
                    edge="start"
                    aria-label="menu"
                    onClick={handleMenuOpen}
                  >
                    <MenuIcon />
                  </IconButton>
                  <Menu
                    anchorEl={anchorEl}
                    open={Boolean(anchorEl)}
                    onClose={handleMenuClose}
                  >
                    <MenuItem>Live</MenuItem>
                    <MenuItem>Sequence</MenuItem>
                    <MenuItem>Layout</MenuItem>
                    <MenuItem>More</MenuItem>
                  </Menu>
                </Toolbar>
              </AppBar>

              {/* User Info */}
              <Box display="flex" alignItems="center" justifyContent="space-between">
                <Typography variant="h6">Username</Typography>
                <Avatar alt="User Avatar" src="/path-to-avatar.jpg" />
                <IconButton color="inherit">
                  <i className="fa fa-angle-down" aria-hidden="true" />
                </IconButton>
              </Box>
            </Paper>
          </Box>
        </Container>
      </div>
    </ThemeProvider>
  );
};

export default MainPage;
