import React, { useEffect, useState } from 'react';
import { Container, Grid, List, ListItem, ListItemIcon, ListItemText,Avatar,InputBase,Toolbar,Hidden} from '@mui/material';
import { Home, Inbox, Mail, LiveTv, ViewComfy, Timeline, MoreHoriz } from '@mui/icons-material';
import './App.css'; 
import { Link } from 'react-router-dom';
import logo from './assets/secura.png';
import customSearchIcon from './assets/Search.png';
import customNotificationsIcon from './assets/Group.png';

const App = () => {
  const [userData, setUserData] = useState({ name: '', avatar: '' });
  const [searchVisible, setSearchVisible] = useState(false);

  const toggleSearch = () => {
    setSearchVisible(!searchVisible);
  };


  useEffect(() => {
    fetchUserData().then(data => {
      setUserData(data);
    });
  }, []);

  const fetchUserData = async () => {
    const response = await fetch('https://jsonplaceholder.typicode.com/users/1');
    const data = await response.json();
    return data;
  };


  const pageStyles = {
    backgroundColor: '#111115',
    minHeight: '100vh',
    display: 'flex',
    overflow: 'hidden',
  };


  const sidebarStyles = {
    backgroundColor: '#151515',
    width: '8vw',
    padding: '16px',
    minHeight: '100vh',
    marginLeft: '-130px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    overflow: 'hidden',
  };


  const logoStyles = {
    width: '3vw',
    height: '6vh',
    marginBottom: '20px',
  };

  const secondSidebarStyles = {
    backgroundColor: '#0D0D0F',
    width: '16vw',
    minHeight: '100vh',
    color: 'white',
    marginBottom:"30px",
    flexDirection: 'column',
    alignItems: 'center',
    overflow: 'hidden',
  };


  const responsiveStyles = {
    '@media (max-width: 768px)': {
      sidebarStyles: {
        width: '25vw', 
      },
      secondSidebarStyles: {
        width: '20vw', 
      },
    },
  };


  return (
    <div style={pageStyles}>
      <Container>
        <Grid container>
          <Grid sx={{ ...sidebarStyles, ...responsiveStyles.sidebarStyles }}>
            <img src={logo} alt="Logo" style={logoStyles} />

            <List>
              <ListItem button >
                <ListItemIcon sx={{ color: 'white', marginLeft: '25px' }}>
                  <Home />
                </ListItemIcon>
              </ListItem>
              <ListItem button >
                <ListItemIcon sx={{ color: 'white', marginLeft: '25px' }}>
                  <Inbox />
                </ListItemIcon>
              </ListItem>
              <ListItem button >
                <ListItemIcon sx={{ color: 'white', marginLeft: '25px' }}>
                  <Mail />
                </ListItemIcon>
              </ListItem>
            </List>
          </Grid>
          <Grid sx={{ ...secondSidebarStyles, ...responsiveStyles.secondSidebarStyles }}>
            <List>
             <div style={{ color: 'white', display: 'flex', alignItems:'center',marginLeft:"10px",marginTop:'5px' }}>
             <div>{userData.name}</div>
            <Avatar src={userData.avatar} alt="User Avatar" sx={{marginLeft:"50px",marginTop:'5px'}}/>
             </div>
             <div style={{marginTop:"20px",textDecoration:"none"}}>
             <ListItem button sx={{textDecoration:"none"}}>
        <ListItemIcon sx={{ color: 'whitesmoke' }}>
          <LiveTv />
        </ListItemIcon>
        <Link to="/live" sx={{textDecoration:'none'}}>
          <ListItemText primary="Live" sx={{ color: 'white' }} />
        </Link>
      </ListItem>
      <ListItem button>
        <ListItemIcon sx={{ color: 'white' }}>
          <ViewComfy />
        </ListItemIcon>
        <Link to="/layout" sx={{}}>
          <ListItemText primary="Layout" sx={{ color: 'white' ,textDecoration:"none"}}/>
        </Link>
      </ListItem>
      <ListItem button>
        <ListItemIcon sx={{ color: 'white' }}>
          <Timeline />
        </ListItemIcon>
        <Link to="/sequence">
          <ListItemText primary="Sequence" sx={{ color: 'white' }}/>
        </Link>
      </ListItem>
      <ListItem button>
        <ListItemIcon sx={{ color: 'white' }}>
          <MoreHoriz />
        </ListItemIcon>
        <Link  sx={{textDecoration: 'none',}}to="/more" >
          <ListItemText primary="More" sx={{ color: 'white' , textDecoration: 'none',}}/>
        </Link>
      </ListItem>
              </div>
            </List>
          </Grid>
          <Grid item xs={6}>

          <Toolbar>
          <div style={{ display: 'flex', alignItems: 'center', width: '100%', marginLeft: '80%' }}>
          <Hidden>
            <div style={{ flex: 1 }}>
              {searchVisible ? (
                <InputBase
                  placeholder=" Search..."
                  inputProps={{ 'aria-label': 'search' }}
                  fullWidth
                  sx={{
                    backgroundColor: 'white',
                    color: 'black',
                    borderRadius: '25px',
                    height: '40px',
                    fontSize: '16px',
                  }}
                />
              ) : null}
            </div>
          </Hidden>
          <div style={{ margin: '0 10px' }}>
            <img
              src={customSearchIcon}
              alt="Search"
              onClick={toggleSearch}
              style={{
                cursor: 'pointer',
                width: '30px',
                height: '30px',
              }}
            />
          </div>


            <div style={{ margin: '0 5px' }}>
              <img
                src={customNotificationsIcon}
                alt="Notifications"
                style={{
                  width: '30px',
                  height: '30px',
                }}
              />
            </div>

        </div>
        </Toolbar>
        </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default App;
